/**
 * Created by hao.cheng on 2017/4/16.
 */
import React from 'react';
import { Table, Button, Row, Col, Card, notification, Space } from 'antd';
import BreadcrumbCustom from '../BreadcrumbCustom';
import { TableRowSelection } from 'antd/lib/table';
import { get } from '../../service/tools';

class AsynchronousTable extends React.Component {
    state = {
        selectedRowKeys: [], // Check here to configure the default column
        loading: false,
        data: [],
        filteredInfo: {name:""},
    };

    // eslint-disable-next-line react/sort-comp
    columns = [
        {
            title: 'Id',
            dataIndex: 'id',
            width: 40,
            sorter: true,
        },
        {
            title: '账号',
            dataIndex: 'adminId',
            width: 80,
        },
        {
            title: '等级',
            dataIndex: 'role',
            width: 80,
            render: (text: number) => <div>{text === 2 ? '普通管理员' : '超级管理员'}</div>,
            filteredValue: this.state.filteredInfo.name || null,
            filters: [
                { text: 'London', value: 'London' },
                { text: 'New York', value: 'New York' },
            ],
            onFilter: (value: any, record: any) => record.relo.includes(value),
        },
        {
            title: '状态',
            dataIndex: 'deleteFlag',
            width: 80,
            render: (text: number) => <div>{text === 0 ? '正常' : '封禁'}</div>,
        },
        {
            title: '创建时间',
            dataIndex: 'createTime',
            width: 200,
        },
    ];

    componentDidMount() {
        this.start();
    }
    start = () => {
        this.setState({ loading: true });
        get({
            url: 'http://localhost:8080/admin/findall',
        }).then((res) => {
            if (!res) {
                notification.open({
                    message: '后台异常',
                });
                return 0;
            }
            if (res.rspCode !== '200') {
                notification.open({
                    message: res.rspMsg,
                });
            } else {
                this.setState({
                    data: res.data,
                });
                this.setState({ loading: false });
            }
        });
    };
    onSelectChange = (selectedRowKeys: string[]) => {
        console.log('selectedRowKeys changed: ', selectedRowKeys);
        this.setState({ selectedRowKeys });
    };
    render() {
        const { loading, selectedRowKeys } = this.state;
        const rowSelection = {
            selectedRowKeys,
            onChange: this.onSelectChange,
        };
        const hasSelected = selectedRowKeys.length > 0;
        return (
            <div className="gutter-example">
                <BreadcrumbCustom first="表格" second="异步表格" />
                <Row gutter={16}>
                    <Col className="gutter-row" md={24}>
                        <div className="gutter-box">
                            <Card title="管理员管理" bordered={false}>
                                <div style={{ marginBottom: 16 }}>
                                    <Button
                                        type="primary"
                                        onClick={this.start}
                                        disabled={loading}
                                        loading={loading}
                                    >
                                        Reload
                                    </Button>
                                    <span style={{ marginLeft: 8 }}>
                                        {hasSelected
                                            ? `Selected ${selectedRowKeys.length} items`
                                            : ''}
                                    </span>
                                </div>
                                <Space style={{ marginBottom: 16 }}>
                                    <Button onClick={this.setAgeSort}>Sort age</Button>
                                    <Button onClick={this.clearFilters}>Clear filters</Button>
                                    <Button onClick={this.clearAll}>
                                        Clear filters and sorters
                                    </Button>
                                </Space>
                                <Table
                                    rowSelection={rowSelection as TableRowSelection<any>}
                                    columns={columns}
                                    dataSource={this.state.data}
                                />
                                );
                            </Card>
                        </div>
                    </Col>
                </Row>
            </div>
        );
    }
}

export default AsynchronousTable;
