/**
 * Created by hao.cheng on 2017/4/15.
 */
import React, { Component } from 'react';
import { Button, Modal, Form, Input, Radio, notification } from 'antd';
import { FormProps } from 'antd/lib/form';
import { get, post } from '../../service/tools';
import umbrella from 'umbrella-storage';
const FormItem = Form.Item;

type CollectionCreateFormProps = {
    visible: boolean;
    onCancel: () => void;
    onCreate: () => void;
    ref: any;
    data:any
} & FormProps;

const CollectionCreateForm: any = Form.create()((props: CollectionCreateFormProps) => {
    const { visible, onCancel, onCreate, form , data} = props;
    const { getFieldDecorator } = form!;
    return (
        <Modal
            visible={visible}
            title="修改管理员信息"
            okText="提交"
            cancelText="取消"
            onCancel={onCancel}
            onOk={onCreate}
        >
            <Form layout="vertical">
                <FormItem label="账号">
                    {getFieldDecorator('adminId', {
                        rules: [{ required: true, message: '请输入账号!' }],
                        initialValue:data.adminId
                    })(<Input />)}
                </FormItem>
                <FormItem label="密码">
                    {getFieldDecorator('adminPassword', {
                        rules: [{ required: true, message: '请输入密码!' }],
                        initialValue:data.adminPassword
                    })(<Input />)}
                </FormItem>
                <FormItem
                    className="collection-create-form_last-form-item"
                    style={{ marginBottom: 0 }}
                >
                    {getFieldDecorator('role', {
                        initialValue: data.role
                    })(
                        <Radio.Group>
                            <Radio value="1">超级管理员</Radio>
                            <Radio value="2">普通管理员</Radio>
                        </Radio.Group>
                    )}
                </FormItem>
                <FormItem
                    className="collection-create-form_last-form-item"
                    style={{ marginBottom: 0 }}
                >
                    {getFieldDecorator('role', {
                        initialValue: data.state,
                    })(
                        <Radio.Group>
                            <Radio value="0">正常</Radio>
                            <Radio value="1">封禁</Radio>
                        </Radio.Group>
                    )}
                </FormItem>
            </Form>
        </Modal>
    );
});

class UpdateForm extends Component {

    // eslint-disable-next-line react/sort-comp
    componentDidMount(){
        this.props.onRef(this)
    }

    
    state = {
        visible: false,
        data:[]
    };
    form: any;
    showModal = () => {
        get({
            url: 'http://localhost:8080/admin/find?username='+umbrella.getLocalStorage('user').adminId,
        }).then((res) => {
            if (!res) {
                notification.open({
                    message: '后台异常',
                });
                return 0;
            }
            if (res.rspCode !== '200') {
                notification.open({
                    message: res.rspMsg,
                });
            } else {
               this.setState({
                   data:res.data
               })
            }
        },)
        this.setState({ visible: true });
    };
    handleCancel = () => {
        this.setState({ visible: false });
    };
    handleCreate = () => {
        const form = this.form;
        form.validateFields((err: any, values: any) => {
            values.role = parseInt(values.role)
            values.deleteFlag = 0
            values.state = 0
            
            if (!err) {
                post({
                    url: 'http://localhost:8080/admin/create',
                    data: values,
                }).then((res) => {
                    if (!res) {
                        notification.open({
                            message: '后台异常',
                        });
                        return 0;
                    }
                    if (res.rspCode !== '200') {
                        notification.open({
                            message: res.rspMsg,
                        });
                    } else {
                        notification.open({
                            message: res.rspMsg,
                        });
                        form.resetFields();
                        this.setState({ visible: false });
                    }
                });
            } else {
                notification.open({
                    message: '修改失败',
                });
                return 0;
            }
           
        });
    };
    saveFormRef = (form: any) => {
        this.form = form;
    };
    render() {
        return (
            <div>
                <CollectionCreateForm
                    ref={this.saveFormRef}
                    visible={this.state.visible}
                    onCancel={this.handleCancel}
                    onCreate={this.handleCreate}
                />
            </div>
        );
    }
}

export default UpdateForm;
